<span class="line"><span style="color:#A6ACCD;">  plugins</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">vue()</span><span style="color:#89DDFF;">],</span></span>
